import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import ReviewForm from './ReviewForm';
import ServiceList from './ServiceList';
import Home from './Home';
import SearchFilter from './SearchFilter';

const App = () => {
  const [services, setServices] = useState([
    { id: 1, name: 'Service 1' },
    { id: 2, name: 'Service 2' },
    { id: 3, name: 'Service 3' },
    // Add more services as needed
  ]);

  const [filteredServices, setFilteredServices] = useState([]);

  const handleFilter = (filterValue) => {
    // Filter services based on the filterValue
    const filtered = services.filter(service =>
      service.name.toLowerCase().includes(filterValue.toLowerCase())
    );
    setFilteredServices(filtered);
  };

  return (
    <Router>
      <div className="container mx-auto px-4">
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/signup">
            <Signup />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/review/:serviceId">
            <ReviewForm />
          </Route>
          <Route path="/services">
            {/* Pass filteredServices as a prop to ServiceList */}
            <ServiceList services={filteredServices.length > 0 ? filteredServices : services} />
          </Route>
          <Route path="/search">
            {/* Pass handleFilter as a prop to SearchFilter */}
            <SearchFilter onFilter={handleFilter} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
