import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import ReviewForm from './ReviewForm';
import Home from './Home';
import SearchExistingServices from './SearchExistingServices'; // Check the correct path
import AddNewService from './AddNewService'; // Check the correct path

const App = () => {
  // Your state and other logic here

  return (
    <Router>
      <div className="container mx-auto px-4">
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/signup">
            <Signup />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/review/:serviceId">
            <ReviewForm />
          </Route>
          <Route path="/search">
            <SearchExistingServices services={services} /> {/* Check if services are correctly passed */}
          </Route>
          <Route path="/add-service">
            <AddNewService onAddService={handleAddService} /> {/* Check if onAddService is correctly passed */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
