import React, { useState } from 'react';

const Signup = ({ setUser }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirmation, setPasswordConfirmation] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("/signup", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        password,
        password_confirmation: passwordConfirmation,
      }),
    }).then((r) => {
      if (r.ok) {
        // Redirect to login or handle successful sign-up
      }
    });
  };

  return (
    <div className="h-screen flex justify-center items-center bg-skyblue">
      <div className="max-w-md mx-auto p-8 bg-white rounded shadow-md">
        <h2 className="text-2xl font-bold mb-4">Sign Up</h2>
        <form onSubmit={handleSubmit}>
          {/* Input fields for username, password, and password confirmation */}
          {/* Add appropriate onChange handlers */}
          {/* Add required attribute to inputs */}
          {/* Add button to submit the form */}
        </form>
      </div>
    </div>
  );
};

export default Signup;
