import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home'; // Import Home component
import About from './About';
import Signup from './Signup';
import Login from './Login';
import AddNewService from './AddNewService';
import Footer from './Footer';
import ReviewForm from './ReviewForm';

const App = () => {
  const [user, setUser] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  // Function to toggle between light and dark themes
  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogout = () => {
    // Implement logout logic here
    fetch("/logout", {
      method: "DELETE",
    }).then(() => setUser(null)); // Clear user state after logout
  };

  return (
    <div>
      <Router>
        <Navbar onLogout={handleLogout} userId={user?.id} toggleDarkMode={toggleDarkMode} darkMode={darkMode} /> {/* Pass userId, toggleDarkMode, and darkMode to Navbar */}
        <Switch>
          <Route exact path="/" render={(props) => <Home {...props} darkMode={darkMode} />} /> {/* Pass darkMode to Home component */}
          <Route path="/about" component={About} />
          <Route path="/signup" render={(props) => <Signup {...props} setUser={setUser} />} />
          <Route path="/login" render={(props) => <Login {...props} setUser={setUser} />} />
          <Route path="/add-service" render={(props) => <AddNewService {...props} onAddService={() => {}} />} />
          <Route path="/add-review/:serviceId" component={ReviewForm} />
        </Switch>
      </Router>
      <Footer />
    </div>
  );
};

export default App;
