import React, { useState, useEffect } from 'react';

const ReviewForm = ({ serviceId }) => {
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [userId, setUserId] = useState('');
  const [error, setError] = useState(null);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch(`/services/${serviceId}/reviews`)
      .then(response => response.json())
      .then(data => setReviews(data))
      .catch(error => console.error('Error fetching reviews:', error));
  }, [serviceId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/services/${serviceId}/reviews`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          rating,
          comment,
          user_id: userId // Include user_id in the request body
        }),
      });
      if (response.ok) {
        // Review submitted successfully
        // You may want to refresh the page or update the list of reviews
        setRating('');
        setComment('');
        setUserId('');
        window.alert('Review submitted successfully!');
        // Refresh the list of reviews
        fetch(`/services/${serviceId}/reviews`)
          .then(response => response.json())
          .then(data => setReviews(data))
          .catch(error => console.error('Error fetching reviews:', error));
      } else {
        const errorData = await response.json();
        setError(errorData.error);
      }
    } catch (error) {
      console.error("Error:", error);
      setError("An error occurred. Please try again later.");
    }
  };

  const handleDeleteReview = async (reviewId) => {
    try {
      const response = await fetch(`/services/${serviceId}/reviews/${reviewId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        // Remove the deleted review from the list
        setReviews(reviews.filter(review => review.id !== reviewId));
        window.alert('Review deleted successfully!');
      } else {
        console.error('Failed to delete review');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleEditReview = async (reviewId, newComment) => {
    try {
      const response = await fetch(`/services/${serviceId}/reviews/${reviewId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          comment: newComment,
        }),
      });
      if (response.ok) {
        // Update the comment in the list of reviews
        setReviews(reviews.map(review => {
          if (review.id === reviewId) {
            return { ...review, comment: newComment };
          }
          return review;
        }));
        window.alert('Review updated successfully!');
      } else {
        console.error('Failed to update review');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-8 mb-20">
      <h2 className="text-2xl font-bold mb-4">Write a Review</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
      </form>

      {/* Display reviews in table format */}
      <div>
        <h2 className="text-2xl font-bold mb-4">Reviews</h2>
        <table className="min-w-full">
          <thead>
            <tr>
              <th className="px-4 py-2">Rating</th>
              <th className="px-4 py-2">Comment</th>
              <th className="px-4 py-2">User ID</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {reviews.map(review => (
              <tr key={review.id}>
                <td className="border px-4 py-2">{review.rating}</td>
                <td className="border px-4 py-2">
                  <input 
                    type="text" 
                    value={review.comment} 
                    onChange={(e) => handleEditReview(review.id, e.target.value)} 
                    className="border p-1 rounded"
                  />
                </td>
                <td className="border px-4 py-2">{review.user_id}</td>
                <td className="border px-4 py-2">
                  <button onClick={() => handleDeleteReview(review.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ReviewForm;
