import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import ReviewForm from './ReviewForm';
import Home from './Home';
import SearchExistingServices from './SearchExistingServices'; // Import the SearchExistingServices component
import AddNewService from './AddNewService'; // Import the AddNewService component

const App = () => {
  const [services, setServices] = useState([
    { id: 1, name: 'Service 1' },
    { id: 2, name: 'Service 2' },
    { id: 3, name: 'Service 3' },
    // Add more services as needed
  ]);

  const handleAddService = (newService) => {
    // Add the new service to the list of services
    setServices(prevServices => [...prevServices, newService]);
    // You can also handle saving the new service to the database here
    console.log("New service added:", newService);
  };

  return (
    <Router>
      <div className="container mx-auto px-4">
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/signup">
            <Signup />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/review/:serviceId">
            <ReviewForm />
          </Route>
          <Route path="/search">
            <SearchExistingServices services={services} /> {/* Render SearchExistingServices component */}
          </Route>
          <Route path="/add-service">
            <AddNewService onAddService={handleAddService} /> {/* Render AddNewService component */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
