import React, { useState } from 'react';

const ServiceList = ({ services }) => {
  if (!services || !Array.isArray(services)) {
    // Handle the case where services is undefined or not an array
    return <p>No services available</p>;
  }

  // eslint-disable-next-line no-unused-vars
  const [newServiceName, setNewServiceName] = useState('');
  // eslint-disable-next-line no-unused-vars
  const [newServiceCategory, setNewServiceCategory] = useState('');
  // eslint-disable-next-line no-unused-vars
  const [newServiceRating, setNewServiceRating] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("/services", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name: newServiceName,
          category: newServiceCategory,
          rating: newServiceRating
        }),
      });
      if (response.ok) {
        // Service added successfully
        // You may want to refresh the service list
      } else {
        const errorData = await response.json();
        setError(errorData.error);
      }
    } catch (error) {
      console.error("Error:", error);
      setError("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="mt-8">
      <h2 className="text-2xl font-bold mb-4">Service List</h2>
      <ul>
        {services.map(service => (
          <li key={service.id} className="mb-2">
            <div className="p-4 border rounded shadow">
              <h3 className="font-bold text-lg">{service.name}</h3>
              <p className="text-gray-600">{service.category}</p>
              <p className="text-gray-600">Rating: {service.rating}</p>
              <p className="text-gray-600">Reviews: {service.reviews ? service.reviews.length : 0}</p>
            </div>
          </li>
        ))}
      </ul>
      <div className="mt-8">
        <h2 className="text-2xl font-bold mb-4">Add New Service</h2>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Service Name"
            value={newServiceName}
            onChange={(e) => setNewServiceName(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="text"
            placeholder="Category"
            value={newServiceCategory}
            onChange={(e) => setNewServiceCategory(e.target.value)}
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <input
            type="number"
            placeholder="Rating"
            value={newServiceRating}
            onChange={(e) => setNewServiceRating(e.target.value)}
            min="1"
            max="5"
            className="block w-full mb-2 p-2 border rounded"
            required
          />
          <button
            type="submit"
            className="w-full bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
          >
            Add Service
          </button>
        </form>
      </div>
    </div>
  );
};

export default ServiceList;
