import React, { useState } from 'react';
import SearchExistingServices from './SearchForm';

const AddNewService = ({ onAddService }) => {
  const [newServiceName, setNewServiceName] = useState('');
  const [services, setServices] = useState([]);

  const handleAddService = async () => {
    if (newServiceName.trim() === '') {
      alert('Please enter a service name');
      return;
    }

    try {
      // Make a POST request to add the new service
      const response = await fetch('/services', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: newServiceName }),
      });
      if (response.ok) {
        const newService = await response.json();
        setServices(prevServices => [...prevServices, newService]);
        onAddService(newService);
        setNewServiceName('');
      } else {
        console.error('Failed to add new service');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleDeleteService = async (id) => {
    try {
      // Make a DELETE request to remove the service
      const response = await fetch(`/services/${id}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setServices(prevServices => prevServices.filter(service => service.id !== id));
      } else {
        console.error('Failed to delete service');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <div className="max-w-md mx-auto mt-8">
        <h2 className="text-2xl font-bold mb-4">Add New Service</h2>
        <SearchExistingServices />
        <input
          type="text"
          placeholder="Enter service name"
          value={newServiceName}
          onChange={e => setNewServiceName(e.target.value)}
          className="block w-full mb-2 p-2 border rounded"
        />
        <button
          onClick={handleAddService}
          className="w-full bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-2"
        >
          Add Service
        </button>
        {services.map(service => (
          <div key={service.id} className="flex items-center mt-4">
            <span className="mr-2">{service.name}</span>
            <button onClick={() => handleDeleteService(service.id)} className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded">Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AddNewService;
