import React, { useState } from 'react';
import ServiceList from './ServiceList'; // Import the ServiceList component

const SearchFilter = ({ services, onFilter, onDeleteService, onLogout }) => {
  const [filter, setFilter] = useState('');

  const handleFilterChange = (e) => {
    const filterValue = e.target.value;
    setFilter(filterValue);
    onFilter(filterValue);
  };

  const handleLogout = () => {
    onLogout();
  };

  return (
    <div className="max-w-md mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Search and Filter</h2>
      <input
        type="text"
        placeholder="Search..."
        value={filter}
        onChange={handleFilterChange}
        className="block w-full mb-2 p-2 border rounded"
      />
      <button
        onClick={handleLogout}
        className="w-full bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mt-4 mr-2"
      >
        Logout
      </button>
      <ServiceList services={services} onDeleteService={onDeleteService} /> {/* Render ServiceList with services and onDeleteService */}
    </div>
  );
};

export default SearchFilter;
