import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import ReviewForm from './ReviewForm';
import ServiceList from './ServiceList';
import Home from './Home';
import SearchFilter from './SearchFilter';

const App = () => {
  const [services, setServices] = useState([
    { id: 1, name: 'Service 1' },
    { id: 2, name: 'Service 2' },
    { id: 3, name: 'Service 3' },
    // Add more services as needed
  ]);

  const [filteredServices, setFilteredServices] = useState([]); // State for filtered services

  const handleFilter = (filterValue) => {
    // Filter services based on the filterValue
    const filtered = services.filter(service =>
      service.name.toLowerCase().includes(filterValue.toLowerCase())
    );
    setFilteredServices(filtered);
  };

  const handleDeleteService = (id) => {
    // Delete the service with the given id
    const updatedServices = services.filter(service => service.id !== id);
    setServices(updatedServices);
    // Also update filtered services if necessary
    const updatedFilteredServices = filteredServices.filter(service => service.id !== id);
    setFilteredServices(updatedFilteredServices);
  };

  const handleLogout = () => {
    // Implement logout logic here
    console.log('Logged out');
  };

  return (
    <Router>
      <div className="container mx-auto px-4">
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/signup">
            <Signup />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/review/:serviceId">
            <ReviewForm />
          </Route>
          <Route path="/services">
            <ServiceList services={filteredServices.length > 0 ? filteredServices : services} onDeleteService={handleDeleteService} />
          </Route>
          <Route path="/search">
            <SearchFilter services={services} onFilter={handleFilter} onDeleteService={handleDeleteService} onLogout={handleLogout} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
