import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Signup from './Signup';
import Login from './Login';
import ReviewForm from './ReviewForm';
import Home from './Home';
import SearchExistingServices from './SearchExistingServices.js'; // Check the correct path
import AddNewService from './AddNewService'; // Check the correct path

const App = () => {
  const [services, setServices] = useState([]);

  const handleAddService = (newService) => {
    setServices(prevServices => [...prevServices, newService]);
  };

  const handleDeleteService = (id) => {
    setServices(prevServices => prevServices.filter(service => service.id !== id));
  };

  return (
    <Router>
      <div className="container mx-auto px-4">
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/signup">
            <Signup />
          </Route>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/review/:serviceId">
            <ReviewForm />
          </Route>
          <Route path="/search">
            <SearchExistingServices services={services} />
          </Route>
          <Route path="/add-service">
            <AddNewService onAddService={handleAddService} />
          </Route>
        </Switch>
        {/* Display added services with delete functionality */}
        {services.length > 0 && (
          <div className="mt-4">
            <h2 className="text-xl font-bold mb-2">Added Services</h2>
            <ul>
              {services.map(service => (
                <li key={service.id}>
                  {service.name}
                  <button onClick={() => handleDeleteService(service.id)} className="ml-2 text-red-600">Delete</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
