import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Signup from './Signup';
import Login from './Login';
import AddNewService from './AddNewService';
import Footer from './Footer';
import ReviewForm from './ReviewForm'; // Import the ReviewForm component

const App = () => {
  // eslint-disable-next-line 
  const [user, setUser] = useState(null);

  // Function to handle logout
  const handleLogout = () => {
    fetch("/logout", {
      method: "DELETE",
    }).then(() => setUser(null)); // Clear user state after logout
  };

  return (
    <div>
      <Router>
        <Navbar onLogout={handleLogout} /> {/* Pass handleLogout function to Navbar */}
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/signup" render={(props) => <Signup {...props} setUser={setUser} />} />
          <Route path="/login" render={(props) => <Login {...props} setUser={setUser} />} />
          <Route path="/add-service" render={(props) => <AddNewService {...props} />} />
          <Route path="/add-review/:serviceId" component={ReviewForm} />
        </Switch>
      </Router>
      <Footer />
    </div>
  );
};

export default App;
