import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ onLogout, onDeleteUser, userId }) => {
  const handleDeleteUser = async () => {
    try {
      const response = await fetch(`/delete_user/${userId}`, { // Interpolate userId into the URL
        method: "DELETE",
      });
      if (response.ok) {
        onDeleteUser(); // Call the onDeleteUser callback to update the state
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Delete user error:', error);
    }
  };

  return (
    <nav className="bg-gray-800 p-4">
      <div className="container mx-auto">
        <div className="flex justify-between items-center">
          <Link to="/" className="text-white text-xl font-bold">Local Services Platform</Link>
          <ul className="flex space-x-4">
            <li><Link to="/" className="text-white">Home</Link></li>
            <li><Link to="/about" className="text-white">About</Link></li>
            <li><Link to="/signup" className="text-white">Sign Up</Link></li>
            <li><Link to="/login" className="text-white">Login</Link></li>
            <li><Link to="/add-service" className="text-white">Add New Service</Link></li>
            <li>
              <button onClick={onLogout} className="text-white hover:bg-gray-700 px-3 py-2 rounded-md text-sm font-medium">
                Logout
              </button>
            </li>
            <li>
              <button onClick={handleDeleteUser} className="text-white hover:bg-gray-700 px-3 py-2 rounded-md text-sm font-medium">
                Manage User
              </button>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
