import React, { useState } from 'react';

const SearchFilter = ({ onFilter, onLogout, services, onDelete }) => {
  const [filter, setFilter] = useState('');

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
    onFilter(e.target.value);
  };

  const handleLogout = () => {
    // Call the onLogout function passed as a prop
    onLogout();
  };

  const handleDelete = (serviceId) => {
    // Call the onDelete function passed as a prop
    onDelete(serviceId);
  };

  return (
    <div className="max-w-md mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Search and Filter</h2>
      <input
        type="text"
        placeholder="Search..."
        value={filter}
        onChange={handleFilterChange}
        className="block w-full mb-2 p-2 border rounded"
      />
      {services.map((service) => (
        <div key={service.id} className="flex items-center justify-between border rounded p-2 mb-2">
          <div>
            <p className="font-bold">{service.name}</p>
            <p>{service.category}</p>
          </div>
          <button
            onClick={() => handleDelete(service.id)}
            className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
          >
            Delete
          </button>
        </div>
      ))}
      <button
        onClick={handleLogout}
        className="w-full bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mt-4"
      >
        Logout
      </button>
    </div>
  );
};

export default SearchFilter;
