import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './Navbar';
import Home from './Home';
import About from './About';
import Signup from './Signup';
import Login from './Login';
import AddNewService from './AddNewService';
import Footer from './Footer';
import ReviewForm from './ReviewForm';

const App = () => {
  const [user, setUser] = useState(null);

  const handleLogout = () => {
    // Implement logout logic here
    fetch("/logout", {
      method: "DELETE",
    }).then(() => setUser(null)); // Clear user state after logout
  };

  const handleDeleteUser = async () => {
    try {
      // Implement user deletion logic here
      const response = await fetch(`/delete_user/${user?.id}`, { // Adjusted endpoint
        method: "DELETE",
      });
      if (response.ok) {
        setUser(null); // Clear user state after deletion
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <Router>
        <Navbar onLogout={handleLogout} onDeleteUser={handleDeleteUser} userId={user?.id} /> {/* Pass userId to Navbar */}
        <Switch>
          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/signup" render={(props) => <Signup {...props} setUser={setUser} />} />
          <Route path="/login" render={(props) => <Login {...props} setUser={setUser} />} />
          <Route path="/add-service" render={(props) => <AddNewService {...props} onAddService={() => {}} />} />
          <Route path="/add-review/:serviceId" component={ReviewForm} />
        </Switch>
      </Router>
      <Footer />
    </div>
  );
};

export default App;
